package week1.day2;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         
		int s = 13;
		
		if(s%2==1)
		{
			System.out.println("It is a prime number");
		}
		else
		{
			System.out.println("It is not a  prime number");
		}
	}

}
